import java.util.StringTokenizer;
import java.util.BitSet;
import java.io.BufferedReader;
import java.util.Collections;
import java.io.InputStream;


/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 *
 * @author kessido
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        TaskE solver = new TaskE();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskE {
        BitSet currentGroup;
        BitSet currentOtherGroup;
        int maxMinSize = -1;
        ArrayList<Integer> options;
        int n;
        BitSet[] edges;
        long start;
        PrintWriter out;
        int p = 0;
        int pMax = 10;

        boolean stateIsValid() {
            BitSet COG = new BitSet(n);
            COG.or(currentOtherGroup);
            int card = COG.cardinality();
            for (int i = 0; i < n; i++) {
                if (COG.get(i)) {
                    COG.and(edges[i]);
                    if (COG.cardinality() != card) return false;
                    COG.or(currentOtherGroup);
                }
            }
            return true;
        }

        void findOne() {
            if (stateIsValid()) {
                int card = currentGroup.cardinality();
                maxMinSize = Math.max(maxMinSize, Math.min(card, n - card));
                p++;
                if (pMax < p) return;
            }
            for (int i1 = 0; i1 < n && p < pMax && checkTime(); i1++) {
                int i = options.get(i1);
                if (currentOtherGroup.get(i)) {
                    for (int j1 = i1 + 1; j1 < n && p < pMax; j1++) {
                        int j = options.get(j1);
                        if (currentOtherGroup.get(j)) {
                            if (!edges[i].get(j)) {
                                BitSet x = (BitSet) edges[i].clone();
                                x.and(currentGroup);
                                boolean r1 = tryToContinue(i, x);
                                x = (BitSet) edges[j].clone();
                                x.and(currentGroup);
                                r1 |= tryToContinue(j, x);
                                if(!r1) return;
                            }
                        }
                    }
                }
            }
        }

        boolean tryToContinue(int j, BitSet x) {
            if (x.cardinality() == currentGroup.cardinality()) {
                currentOtherGroup.flip(j);
                currentGroup.flip(j);
                findOne();
                currentOtherGroup.flip(j);
                currentGroup.flip(j);
                return true;
            }
            return false;
        }

        public void solve(int testNumber, InputReader in, PrintWriter out) {
            start = System.nanoTime();
            n = in.NextInt();
            int m = in.NextInt();
            edges = new BitSet[n];
            for (int i = 0; i < n; i++) {
                edges[i] = new BitSet(n);
                edges[i].set(i, true);
            }
            for (int i = 0; i < m; i++) {
                int u = in.NextInt() - 1;
                int v = in.NextInt() - 1;
                edges[u].set(v, true);
                edges[v].set(u, true);
            }

            currentGroup = new BitSet(n);
            currentOtherGroup = new BitSet(n);
            currentOtherGroup.set(0, n, true);
            options = new ArrayList<>();
            for (int i = 0; i < n; i++) {
                options.add(i);
            }
            this.out = out;
            while (checkTime()) {
                Collections.shuffle(options);
                p = 0;
                findOne();
            }
            if (maxMinSize == -1) {
                out.println(-1);
            } else {
                out.println(maxMinSize * (maxMinSize - 1) / 2 + (n - maxMinSize) * (n - maxMinSize - 1) / 2);
            }
        }

        boolean checkTime() {
            return (System.nanoTime() - start < 1_800_000_000L);
        }

    }

    static class InputReader {
        BufferedReader reader;
        StringTokenizer tokenizer;

        public InputReader(InputStream stream) {
            reader = new BufferedReader(new InputStreamReader(stream), 32768);
            tokenizer = null;
        }

        public String next() {
            while (tokenizer == null || !tokenizer.hasMoreTokens()) {
                try {
                    tokenizer = new StringTokenizer(reader.readLine(), " \t\n\r\f,");
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
            }
            return tokenizer.nextToken();
        }

        public int NextInt() {
            return Integer.parseInt(next());
        }

    }
}

import java.io.*;
import java.math.BigInteger;
import java.util.*;

public class Main {
    FastScanner in;
    PrintWriter out;

    void solve() {
        Random rnd = new Random(123);
        final long p = (long) 1e9 + 7;
        BigInteger MODULO = BigInteger.valueOf(2).pow(64);
        final long pInv = BigInteger.valueOf(p).modInverse(MODULO).longValue();
        final int MAX = 500010;
        long[] vals = new long[MAX];
        vals[MAX / 2] = 1;
        for (int i = MAX / 2 + 1; i < MAX; i++) {
            vals[i]= vals[i - 1] * p;
        }
        for (int i = MAX / 2 - 1; i >= 0; i--) {
            vals[i] = vals[i + 1] * pInv;
        }
        in.nextInt();
        String s = in.next();
        long[] hashes = new long[s.length() + 1];
        int curPos = MAX / 2;
        int[] myPos = new int[s.length()];
        for (int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            myPos[i] = curPos - MAX / 2;
            hashes[i + 1] = hashes[i];
            if (c == '<') {
                curPos--;
            } else if (c == '>') {
                curPos++;
            } else if (c == '-') {
                hashes[i + 1] -= vals[curPos];
            } else {
                hashes[i + 1] += vals[curPos];
            }
        }
//        System.err.println(Arrays.toString(hashes));
        long expectedHash = hashes[hashes.length - 1];
        long mul = 1, add = 0;
        int realPower = 0;
        HashMap<Long, Integer> count = new HashMap<>();
        long result = 0;
        for (int i = s.length() - 1; i >= 0; i--) {
            long nothingHash = -add * vals[MAX / 2 - realPower];
            long check = nothingHash * mul + add;
            if (check != 0) {
                throw new AssertionError();
            }
            Integer now = count.get(nothingHash);
            count.put(nothingHash, now == null ? 1 : (now + 1));
            char c = s.charAt(i);
            if (c == '<') {
                mul *= pInv;
                realPower--;
                add *= pInv;
            } else if (c == '>') {
                mul *= p;
                realPower++;
                add *= p;
            } else if (c == '-') {
                add--;
            } else {
                add++;
            }
            long need = expectedHash;
//            need *= vals[MAX / 2 - myPos[i]];
            long inMap = (need - add) * vals[MAX / 2 - realPower];
            Integer cou = count.get(inMap);
            if (cou != null) {
                result += cou;
//                System.err.println(i + "-> " + cou);
            }
        }
        out.println(result);
    }

    void run() {
        try {
            in = new FastScanner(new File("Main.in"));
            out = new PrintWriter(new File("Main.out"));

            solve();

            out.close();
        } catch (FileNotFoundException e) {
            e.printStackTrace();
        }
    }

    void runIO() {

        in = new FastScanner(System.in);
        out = new PrintWriter(System.out);

        solve();

        out.close();
    }

    class FastScanner {
        BufferedReader br;
        StringTokenizer st;

        public FastScanner(File f) {
            try {
                br = new BufferedReader(new FileReader(f));
            } catch (FileNotFoundException e) {
                e.printStackTrace();
            }
        }

        public FastScanner(InputStream f) {
            br = new BufferedReader(new InputStreamReader(f));
        }

        String next() {
            while (st == null || !st.hasMoreTokens()) {
                String s = null;
                try {
                    s = br.readLine();
                } catch (IOException e) {
                    e.printStackTrace();
                }
                if (s == null)
                    return null;
                st = new StringTokenizer(s);
            }
            return st.nextToken();
        }

        boolean hasMoreTokens() {
            while (st == null || !st.hasMoreTokens()) {
                String s = null;
                try {
                    s = br.readLine();
                } catch (IOException e) {
                    e.printStackTrace();
                }
                if (s == null)
                    return false;
                st = new StringTokenizer(s);
            }
            return true;
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }

        double nextDouble() {
            return Double.parseDouble(next());
        }
    }

    public static void main(String[] args) {
        new Main().runIO();
    }
}

