using System.Numerics;
using static System.Math;
namespace Program {
    public class Solver {
        Random rnd = new Random();
        public void Solve() {
            var n = ri;
            var s = rs;
            BigInteger MOD = (BigInteger)1e18 + 3;
            BigInteger B = 99431;
            BigInteger invB = BigInteger.ModPow(B, MOD - 2, MOD);
            BigInteger h = 0;
            BigInteger x = 1;
            var hash = new List<BigInteger>() { 0 };
            var vec = new List<BigInteger>() { 1 };
            foreach (var c in s)
            {
                if (c == '+') h = (h + x) % MOD;
                else if (c == '-') h = (h + MOD - x) % MOD;
                else if (c == '>') x = x * B % MOD;
                else x = x * invB % MOD;
                hash.Add((long)h);
                vec.Add((long)x);
            }
            var ans = 0L;
            var map = new HashMap<long, int>();
            map[(long)hash.Last()]++;
            for (int i = n - 1; i >= 0; i--)
            {
                ans += map[(long)((hash.Last() * vec[i] + hash[i]) % MOD)];
