immutable int MN = 600_000;

long mul(long a, long b) {
    if (b == 0) return 0;
    if (b % 2 == 1) return (a + mul(a, b-1)) % Mod;
    else return (2*mul(a, b/2)) % Mod;
}
/*long mul(long a, long b) {
    assert(a < Mod && b < Mod);
    return (a * b) % Mod;
}*/

long pw(long a, long n) {
    long b = 1;
    while (n) {
        if (n & 1) b = mul(b, a);
        a = mul(a, a);
        n >>= 1;
    }
    return b;
}

long[] powB = new long[MN + 1];
long[] powiB = new long[MN + 1];
static this() {
    iB = pw(B, Mod-2);
    assert(mul(iB, B) == 1);
    powB[0] = 1;
    foreach (i; 0..MN) {
        powB[i+1] = mul(powB[i], B);
    }
    powiB[0] = 1;
    foreach (i; 0..MN) {
        powiB[i+1] = mul(powiB[i], iB);
    }
