type Mint = modint::Mint1000000007;

fn main() {
    input!(n: usize, mut s: Chars);
    s.reverse();
    let make = |x: Mint| -> Vec<(Mint, Mint)> {
        let mut ans = vec![(Mint::one(), Mint::zero()); n + 1];
        for (i, &c) in s.iter().enumerate() {
            let (a, b) = ans[i];
            ans[i + 1] = match c {
                '+' => (a, b + Mint::one()),
                '-' => (a, b - Mint::one()),
                '<' => (a * x, b * x),
                '>' => (a / x, b / x),
                _ => panic!(),
            }
        }
        ans
    };

    let keys: Vec<_> = vec![
        12534251,
        141236,
        5374676127,
        865375627,
        856738654763,
        236542672547,
    ]
    .into_iter()
    .map(Mint::from_value)
    .collect();
    let seqs: Vec<_> = keys.iter().copied().map(make).collect();
    let mut btree_map = std::collections::BTreeMap::<Vec<_>, _>::new();
    let mut ans = 0;
    for i in 0..=n {
        if let Some(x) = btree_map.get(
            &(0..keys.len())
                .map(|j| {
                    let (a, b) = seqs[j][i];
                    ((b - seqs[j][n].1) / a).value()
                })
                .collect::<Vec<_>>(),
        ) {
            ans += x;
        }
        *btree_map
            .entry(
                (0..keys.len())
                    .map(|j| {
                        let (a, b) = seqs[j][i];
                        (b / a).value()
                    })
                    .collect(),
            )
            .or_insert(0) += 1;
    }
    println!("{}", ans);
}

