const MOD: i64 = 1_000_000_009;


fn invmod(a: i64, p: i64) -> i64 {
    if a == 1 {
        return 1;
    } else {
        return (1 - p*invmod(p%a, a)) / a + p;
    }
}


fn eat(ch: char, tape: &mut Vec<i64>, pos: &mut Vec<i64>, X: &Vec<i64>, Xinv: &Vec<i64>) {
    match ch {
        '+' => {
            for i in 0..4 {
                tape[i] += pos[i];
                tape[i] %= MOD;
            }
        }
        '-' => {
            for i in 0..4 {
                tape[i] -= pos[i];
                tape[i] += MOD;
                tape[i] %= MOD;
            }
        }
        '>' => {
            for i in 0..4 {
                pos[i] *= X[i];
                pos[i] %= MOD;
            }
        }
        '<' => {
            for i in 0..4 {
                pos[i] *= Xinv[i];
                pos[i] %= MOD;
            }
        }
        _ => panic!()
    }
}



fn main() {
    let cin = stdin();
    let cin = cin.lock();
    let mut sc = Scanner::new(cin);

    let X: Vec<i64> = vec![31, 41, 592, 653];
    let mut Xinv: Vec<i64> = vec![];
    for i in 0..4 {
        Xinv.push(invmod(X[i], MOD));
    }
    let Xinv = Xinv;
    //println!("{:?}, {:?}", X, Xinv);

    let n: usize = sc.read();
    let s: String = sc.read();
    let s = s.chars().collect::<Vec<_>>();
    //println!("{}, {:?}", n, s);
    let mut tape = vec![0, 0, 0, 0];
    let mut pos = vec![1, 1, 1, 1];
    for &ch in s.iter() {
        eat(ch, &mut tape, &mut pos, &X, &Xinv);
    }
    let target = tape;
    //println!("{:?}", target);
    let mut goals = BTreeMap::new();
    let mut tape = vec![0, 0, 0, 0];
    let mut pos = vec![1, 1, 1, 1];
    let mut ans: i64 = 0;
    for &ch in s.iter() {
        {
            let mut goal: Vec<i64> = vec![];
            for i in 0..4 {
                goal.push((tape[i] + pos[i] * target[i]) % MOD);
            }
            let cnt = goals.remove(&goal).unwrap_or(0);
            goals.insert(goal, cnt+1);
        }
        eat(ch, &mut tape, &mut pos, &X, &Xinv);
        ans += *(goals.get(&tape).unwrap_or(&0));
        //println!("{:?}", goals);
    }
    println!("{}", ans);
}

