		flag2 = 1;
	}

 	if(flag1 || flag2){
		return 1;
	} else{
		return 0;
	}
}

int half(int y){
	int temp = y;

	temp /= 2;
	if(temp % 50 != 0){
		temp = temp + abs((temp % 50) - 50);
	}

	return temp;
}

