		if(oh - 17 > 0 || (oh == 17 && om >= 30)){
			flag1 = 1;
		}
	}

	if(ih < 19 || (ih == 19 && im <= 30)){
		if(oh - 19 > 0 || (oh == 19 && om >= 30)){
			flag2 = 1;
		}
	}

	if((flag1 && flag2) || (!flag1 && !flag2)) return 0;
	else if((flag1 && !flag2) || (!flag1 && flag2)) return 1;
}

