int checkhalf(int ih, int im, int oh, int om, int dis){
	int flag1 = 0, flag2 = 0, flag3 = 0;
	if(dis > 40) return 0;

	if(ih < 17 || (ih == 17 && im <= 30)){
		if((oh == 19 && om < 30) || oh < 19){
			flag1 = 1;
		}
	}

	if(ih > 17 || (ih == 17 && im > 30)){
		if((oh == 19 && om >= 30) || oh > 19){
			flag2 = 1;
		}
	}

	if((ih > 17 || (ih == 17 && im > 30)) && ((oh == 19 && om < 30) || oh < 19)){
		flag3 = 1;
	}

	if((flag1 && flag2) || (!flag1 && !flag2) || flag3) return 0;
	else return 1;
}

int half(int y){
	int temp = y;

	temp /= 2;
	if(temp % 50 != 0){
		temp = temp + abs((temp % 50) - 50);
	}

	return temp;
}

