                      {43, 37, 30, 25, 20, 0, 500},
                      {58, 52, 45, 40, 35, 15, 0}};
    int a = 17 * 60 + 30;
    int b = 19 * 60 + 30;

    for(;;){
        int ic[2];
        bool half = false;
