            -- ic[i];

            m += h * 60;
            if(a <= m && m <= b)
                half = true;
        }
        if(ic[1] < ic[0])
            swap(ic[0], ic[1]);

        if(table[ic[1]][ic[0]] <= 40)
            half &= true;

        if(half){
            int a = table[ic[0]][ic[1]];
            if(a % 100 != 0)
                a += 50;
            cout << (a / 2) << endl;
        }else{
            cout << table[ic[0]][ic[1]] << endl;
        }
    }
}

