		scanf(" %d %d", &h1, &m1);
		scanf(" %d", &outic);
		scanf(" %d %d", &h2, &m2);

		int price=p[inic][outic];
		if(half[inic][outic]){
			if(hangaku(h1, m1) || hangaku(h2,m2)){
				price/=2;
				if(price%50!=0){
					price+=50-(price%50);	
				}
			}		
		}
		printf("%d\n", price);
	}
	return 0;
}

