int half(int y){
	int ans;
	double temp = y;
	temp /= 2.0;
	temp = ceil(temp);
	ans = (int)temp;
	if(ans % 50 != 0){
		ans = ans + abs((ans % 50) - 50);
	}
	return ans;
}

