int get_distance(int a,int b) {
	if(a<=b) {
		return ryokin_table[b-1][a-1];
	} else {
		return ryokin_table[a-1][b-1];
	}
}

int get_ryokin(int a,int b) {
	if(a<=b) {
		return ryokin_table[a-1][b-1];
	} else {
		return ryokin_table[b-1][a-1];
	}
}

int is_hangaku_time(int h,int m) {
	if(h==18)return 1;
	if(h==17 && m>=30)return 1;
	if(h==19 && m<=30)return 1;
	return 0;
}

int get_hangaku(int ryokin) {
	int hangaku=ryokin*10/2;
	if(hangaku%500!=0)hangaku+=(500-hangaku%500);
	return hangaku/10;
}

int main(void) {
	int in_ic,out_ic;
	int in_h,in_m;
	int out_h,out_m;
	int is_hangaku;
	int now_ryokin;
	int hangaku_ryokin;
	while(1) {
		scanf("%d",&in_ic);
		if(in_ic==0)break;
		scanf("%d%d",&in_h,&in_m);
		scanf("%d",&out_ic);
		scanf("%d%d",&out_h,&out_m);
		if((is_hangaku_time(in_h,in_m) || is_hangaku_time(out_h,out_m)) &&
			get_distance(in_ic,out_ic)<=40)is_hangaku=1;
		else is_hangaku=0;
		now_ryokin=get_ryokin(in_ic,out_ic);
		hangaku_ryokin=get_hangaku(now_ryokin);
		printf("%d\n",(is_hangaku?hangaku_ryokin:now_ryokin));
	}
	return 0;
}

