int read()
{
	int s=0,t=1; char ch=getchar();
	while(ch<'0'||ch>'9'){if(ch=='-')	t=-1; ch=getchar();}
	while(ch>='0' && ch<='9')	s=s*10+ch-'0',ch=getchar();
	return s*t;
}
int ksm(int bs,int mi)
{
	int ans=1;
	while(mi)
	{
		if(mi&1)	ans=1ll*ans*bs%mdn;
		bs=1ll*bs*bs%mdn; mi>>=1;
	}
	return ans;
}
void upd(int &x,int y){x+=x+y>=mdn?y-mdn:y;}
int fac[N],inv[N],a[N],ans,n,k,r[N],b[N];
char ch[N]; bool vis[N];
void init()
{
	fac[0]=1;
	for(int i=1;i<N;i++)	fac[i]=1ll*fac[i-1]*i%mdn;
	inv[N-1]=ksm(fac[N-1],mdn-2);
	for(int i=N-1;i;i--)	inv[i-1]=1ll*inv[i]*i%mdn;
}
int C(int n,int m){return n<m?0:1ll*fac[n]*inv[m]%mdn*inv[n-m]%mdn;}
int solve(int tot,int dat)
{
	int cur=fac[tot],cnt=0;
	for(int i=1;i<=tot;i++)
	{
		if(a[i]==a[i-1])	cnt++;
		else	cur=1ll*cur*inv[cnt]%mdn, cnt=1;
	}
	cur=1ll*cur*inv[cnt]%mdn;
	int pt=1; memset(vis,0,n+1);
	for(int i=1;i<=k;i++)	if(ch[i]=='r')
		if(pt<=tot)	r[pt]=i,pt++,vis[i]=1;
	if(pt<=tot)	return 0; pt=1;
	for(int i=1;i<=k;i++)	if(ch[i]=='b')
		if(pt<=tot&&r[pt]<i&&a[pt]>=2)	b[pt]=i,pt++,vis[i]=1;
	if(pt<=tot&&a[pt]>=2)	return 0; pt=1; int qwq=a[pt]-2;
	for(int i=1;i<=k;i++)	if(!vis[i])
		if(pt<=tot&&b[pt]<i&&qwq>0)
		{
			qwq--; if(!qwq)	pt++,qwq=a[pt]-2;
		}
	if(qwq>0)	return 0;
	return 1ll*dat*cur%mdn;
}
void dfs(int val,int x,int ful,int wei)
{
	if(ful<wei)	return;
	if(!val)	return upd(ans,solve(x-1,C(ful,wei)));
	for(int i=1;i<=min(val,a[x-1]);i++)	a[x]=i,dfs(val-i,x+1,ful+(i>=2?2:0),wei+2*i);
}
