const int mod=1e9+7;
const int maxn=1005;
int C[maxn][maxn];
int fac[maxn],invfac[maxn];
ll quick(ll a,ll b)
{
    ll ret=1;
    while(b)
    {
        if(b&1) ret=ret*a%mod;
        a=a*a%mod;
        b>>=1;
    }
    return ret;
}
ll inv(ll a){return quick(a,mod-2);}
void init()
{
    fac[0]=1;
    invfac[0]=1;
    for(int i=1;i<maxn;i++)
    {
        fac[i]=1LL*fac[i-1]*i%mod;
        invfac[i]=inv(fac[i]);
    }
    for(int i=0;i<maxn;i++)
    {
        C[i][0]=1;
        for(int j=1;j<=i;j++)
            C[i][j]=(C[i-1][j-1]+C[i-1][j])%mod;
    }
}
string s;
inline int comb(int n,int m)
{
    if(m>n) return 0;
    return C[n][m];
}
vi apos;
int ca=0;
int n,k;
int suf[75];
vi f;
int solve()
{
    if(!f.size()) return 1;
    int need=0;
    int p=0;
    for(int i=f.size()-1;i>=0;i--)
    {
        need+=f[p]-1;
        if(suf[apos[i]]<need) return 0;
        p++;
    }
    int L=f.size()-1;
    int cnt=f.size()+1;
    for(int i=0;i<f.size();i++)
    {
        if(f[i]==1) 
        {
            L++;
            cnt++;
        }
        else {
            L+=2*f[i]-3;
            cnt+=2*f[i]-1;
        }
    }
    //dbg(f);
    int ret=comb(n-L+cnt-1,cnt-1);
    //dbg(ret,L,cnt);
    return ret;
}
int dfs(int r,int mx,int cc,int len=0)
{
    if(len+(int)f.size()-1>n) return 0;
    int ret=1LL*solve()*invfac[cc]%mod*fac[f.size()]%mod;
    if(r==0) return ret;
    if(f.size()>=ca) return ret;
    for(int i=mx;i<=r;i++)
    {
        f.push_back(i);
        int cur=dfs(r-i,i,(i==mx)?cc+1:1,i==1?len+1:len+2*i-3);
        if(i!=mx) cur=1LL*cur*invfac[cc]%mod;
        ret=(ret+cur)%mod;
        f.pop_back();
    }
    return ret;
}
