set<vi> st,nst;
mint brute(int N,string s){
	vi be(N);
	st.insert(be);
	for(char c:s){
		int x=(c=='r'?1:2);
		for(vi v:st){
			rep(l,N) for(int r=l;r<N;r++){
				vi nv=v;
				bool ok=1;
				for(int i=l;i<=r;i++){
					if(v[i]==0&&x==2){
						ok=0;
						break;
					}
					nv[i]=x;
				}
				if(ok) nst.insert(nv);
			}
			nst.insert(v);
		}
		st=nst;
		nst.clear();
	}
	return st.size();
}

