int a[MAXN + 5];

inline int check(int N)
{
	static int pos[MAXN + 5], vis[MAXN + 5];
	memset(vis, 0, sizeof vis);

	int lstr = 0;
	for(int i = 1; i <= N; ++i)
	{
		pos[i] = K + 1;
		for(int j = lstr + 1; j <= K; ++j) if(s[j] == 'r' && !vis[j]) { vis[lstr = pos[i] = j] = 1; break; }
		if(pos[i] == K + 1) return 0;

		if(a[i] > 1)
		{
			for(; pos[i] <= K; ++pos[i]) if(!vis[pos[i]] && s[pos[i]] == 'b') { vis[pos[i]] = 1; break; }
			if(pos[i] == K + 1) return 0;
		}
	}

	for(int i = 1; i <= N; ++i) if(a[i] > 2)
	{
		int cnt = 2;
		for(int j = pos[i] + 1; cnt < a[i] && j <= K; ++j) if(!vis[j]) vis[j] = 1, ++cnt;
		if(cnt < a[i]) return 0;
	}

	return 1;
}

inline int div_into(int N, int M) { return C(N + M - 1, M - 1); }

inline int calc(int N, int S)
{
	int res = fac[N], M = N + 1;
	for(int i = 1, j; i <= N; i = j)
	{
		for(j = i; j <= N && a[j] == a[i]; ++j);
		res = (LL) res * ifac[j - i] % MOD;
		M += (a[i] * 2 - 1) * (j - i);
	}
	return (LL) res * div_into(n - S, M) % MOD;
}

int ans = 0;

inline void dfs(int k, int lst, int S)
{
	if(S > n) return;
	if(check(k)) (ans += calc(k, S)) %= MOD;

	for(int i = 1; i <= lst; ++i) a[k + 1] = i, dfs(k + 1, i, S + (i == 1 ? 1 : i * 2 - 3) + (k > 0));
}

inline void solve()
{
	ans = 0, dfs(0, n, 0);
	printf("%d\n", (ans + MOD) % MOD);
}

