            }
            return 0;
        }
    }


    rep(i,n+1)rep(j,N+1)dp[i][j]=0;
    dp[0][0]=1;
    for(int i=0;i<n;i++){
        for(int j=0;j<N;j++){
            if(X[i]==1){
                for(int k=1;j+k<=N;k++)add(dp[i+1][j+k],dp[i][j]);
            }
            else{
                for(int k=0;2*X[i]-3+k+j<=N;k++){
                    add(dp[i+1][j+2*X[i]-3+k],dp[i][j]*nCk(2*X[i]-2+k,k)%mod);
                }
            }
        }
    }

    int ret=0;
    for(int i=0;i<=N;i++){
        add(ret,dp[n][i]*nCk(N-i+1,n)%mod);
    }

    ret=ret*fact[n]%mod;
    map<int,int>cnt;
    rep(i,n)cnt[X[i]]++;
    for(auto p:cnt)ret=ret*inv[p.se]%mod;
    return ret;
}

