	For(i,1,N-1) For(j,1,i)
		C[i][j]=(C[i-1][j]+C[i-1][j-1])%mo;
}
void solve(int S){
	if (!S) return ++ans,void(0);
	memset(used,0,sizeof(used));
	int rest=S-a[1],h=0,t=*q2=0;
	For(i,1,k)
		if (s[i]=='r') q1[++t]=i;
		else if (s[i]=='b'&&h!=t&&*q2!=rest)
			used[q1[++h]]=used[i]=1,q2[++*q2]=i;
	//printf("%d %d %d\n",rest,*q2,ans);
	//For(i,1,n) printf("%d ",a[i]); puts("");
	if (*q2!=rest) return;
	For(i,1,a[1])
		if (h==t) return;
		else used[q1[++h]]=1;
	//printf("%d %d %d\n",rest,*q2,ans);
	//For(i,1,n) printf("%d ",a[i]); puts("");
	Rep(i,k,1) suf[i]=suf[i+1]+(!used[i]);
	For(i,1,S-a[1]) q2[i]=suf[q2[i]];
	int posi=S-a[1],sum=0,par=0;
	For(i,2,n) For(j,1,a[i]){
		sum+=i-2;
		if (sum>q2[posi]) return;
		--posi; 
	}
	int v=1,s=a[1];
	sum=a[1]+S-1;
	par=S+1+a[1];
	For(i,2,n){
		v=1ll*v*C[s+a[i]][s]%mo;
		sum+=a[i]*(2*i-3);
		par+=a[i]*(2*i-1);
		s+=a[i]; 
	}
	if (sum>n) return;
	//printf("%d %d %d\n",sum,par,v);
	v=1ll*v*C[n-sum+par-1][par-1]%mo;
	ans=(ans+v)%mo;
}
