	{
		if(b&1)ans=(long long)ans*t%Mod;
		t=(long long)t*t%Mod;
		b>>=1;
	}
	return ans;
}
void init()
{
	int N=200;
	fac[0]=1;
	for(int i=1;i<=N;i++)
		fac[i]=(long long)fac[i-1]*i%Mod;
	inv[N]=fpow(fac[N],Mod-2);
	for(int i=N;i>=1;i--)
		inv[i-1]=(long long)inv[i]*i%Mod;
	return ;
}
int C(int x,int y)
{
	return (long long)fac[x]*inv[y]%Mod*inv[x-y]%Mod;
}
