int N, k, tot, ans, fac[maxn + 10], ifac[maxn + 10], inv[maxn + 10];
int a[100], pos[100], cnt[100];
char s[100];
bool vis[100];

void solve(int n) {
	memset(vis, 0, sizeof vis);
	for (int i = 1, p = 1; i <= n; ++i) {
		for (; p <= k && s[p] != 'r'; ++p);
		if (p > k) return;
		vis[p] = 1;
		pos[i] = p++;
	}
	for (int i = 1, p = 1; i <= n; ++i)
		if (a[i] >= 2) {
			for (; p <= k && (p <= pos[i] || s[p] != 'b'); ++p);
			if (p > k) return;
			vis[p] = 1; pos[i] = p++;
		}
	for (int i = 1, p = 1; i <= n; ++i)
		if (a[i] >= 3)
			for (int j = 1; j <= a[i] - 2; ++j) {
				for (; p <= k && (p <= pos[i] || vis[p]); ++p);
				if (p > k) return;
				++p;
			}
	int ans = fac[n];
	memset(cnt, 0, sizeof cnt);
	for (int i = 1; i <= n; ++i)
		++cnt[a[i]];
	for (int i = 1; i <= N; ++i)
		if (cnt[i])
			ans = 1ll * ans * ifac[cnt[i]] % mod;
	int x = N + 1, y = 0;
	for (int i = 1; i <= n; ++i) {
		if (a[i] >= 2) x += 2;
		y += a[i] * 2;
	}
	if (x < y) return;
	ans = 1ll * ans * fac[x] % mod;
	ans = 1ll * ans * ifac[y] % mod;
	ans = 1ll * ans * ifac[x - y] % mod;
	(tot += ans) %= mod;
}

void dfs(int p, int l, int mx) {
	solve(p - 1);
	for (int i = mx; i >= 1; --i)
		if (max(i * 2 - 3, 1) <= l) {
			a[p] = i;
			dfs(p + 1, l - max(i * 2 - 3, 1), i);
		}
}

