inline void init()
{
	C[0][0]=1; REP(i,1,300)REP(j,0,i)C[i][j]=(C[i-1][j]+(j?C[i-1][j-1]:0))%mod;
	jc[0]=jc[1]=jcn[0]=jcn[1]=inv[1]=1;
	REP(i,2,300)jc[i]=(ll)i*jc[i-1]%mod,inv[i]=(ll)(mod-mod/i)*inv[mod%i]%mod,jcn[i]=(ll)jcn[i-1]*inv[i]%mod;
	n=read();k=read(); len=n+1>>1;
	scanf("%s",str+1);
	REP(i,1,k)if(str[i]=='r')rk[++tp]=i;
}

int p[maxn];
int tot;
int wp[maxn],B[maxn];

inline bool check()
{
	static int vis[maxn];
	memset(vis,0,sizeof(int)*(k+1));

	int now=0,num=0;
	DREP(i,tot,0)num+=p[i];
	if(num>tp)return 0;
	REP(i,1,num)vis[rk[i]]=1;

	int it=1;
	DREP(i,tot,0)
	{
		REP(j,1,p[i])
		{
			int x=rk[++now];
			if(i>=1)
			{
				while(it<=k && (it<=x || vis[it] || str[it]=='r'))++it;
				if(it>k)return 0;
				vis[it]=1;
			}
		}
	}

	now=0;
	it=1;
	DREP(i,tot,0)
	{
		//REP(j,1,p[i])
		int NUM=p[i];
		while(NUM--)
		{
			int x=rk[++now];
			int tim=i-1;
			if(i>=2)
			{
				while(tim--)
				{
					while(it<=k && (it<=x || vis[it] ))++it;
					if(it>k)return 0;
					vis[it]=1;
				}
			}
		}
	}
	return 1;
}

int ans;

void dfs(int u,int num,int s,int wz,int N,int pro)
{
	if(u==-1)
	{
		pro=(ll)pro*jc[num]%mod;
		wz++; N+=2;
		//DREP(i,len,0)pro=(ll)pro*jcn[p[i]]%mod;
		ans=(ans+(ll)pro*C[N-1][wz-1])%mod;
		return;
	}
	//int ad=u%4==0?2:u%4==1?0:1;
	REP(j,0,(n-s+1)/(wp[u]+1))
	{
		p[u]=j;
		if(num+j<=tp && check())
			dfs(u-1,num+j,s+j*wp[u]+j,wz+wp[u]*j+j+(u>0?2*j:0),N+(u>0?2*j:0),(ll)pro*jcn[j]%mod);
		p[u]=0;
	}
}

inline void doing()
{
	wp[0]=1;
	REP(i,1,len)
	{
		wp[++tot]=i*2-1; B[tot]=i;
	}
	dfs(tot,0,0,0,n,1);
	printf("%d\n",ans);
}

