int n,m,ans,cnt;
int a[N],b[N],c[N],d[N],f[N],p[N],fc[N],vf[N],iv[N];
char s[N];
void ini(int n){
	iv[1]=fc[0]=vf[0]=1;
	FOR(i,2,n) iv[i]=1ll*(P-P/i)*iv[P%i]%P;
	FOR(i,1,n) fc[i]=1ll*fc[i-1]*i%P,vf[i]=1ll*vf[i-1]*iv[i]%P;
}
int C(int x,int y){return x<y?0:1ll*fc[x]*vf[x-y]%P*vf[y]%P;}
bool work(int L,int s,int t){
	int r=1;
	FOR(i,1,m) c[i]=0,b[i]=f[i];b[L+1]=0;
	//FOR(i,1,L) cout<<b[i]<<' ';cout<<'\n';
	FOR(i,1,m)if(!a[i] && r<=L) b[c[i]=r]--,p[r++]=i;
	if(b[r]) return 1;r=1;
	FOR(i,1,m)if( a[i] && b[r] && p[r]<i) b[c[i]=r]--,p[r++]=i;
	if(b[r]) return 1;r=1;
	FOR(i,1,m)if(!c[i] && b[r] && p[r]<i && !--b[r]) r++;
	int ret=fc[L];
	FOR(i,1,m) ret=1ll*ret*vf[d[i]]%P;
	(ans+=1ll*ret*C(t,2*s)%P)%=P;
}
void dfs(int x,int s,int t){
	if(t<2*s) return;work(x-1,s,t);
	FOR(i,1,min(f[x-1],m-s)) f[x]=i,d[i]++,dfs(x+1,s+i,t+2*(i>=2)),d[i]--;
}
