    int p = 1;
    for(int i = cnt ; i >= 1 ; --i) {
        if(L[p] >= 2) {
            if(!B) return false;
            --B;
            if(R + B < L[p] - 2) return false;
            R -= L[p] - 2;
            if(R < 0) {B += R,R = 0;}
        }
        R += sum[pos[i] - 1][0] - sum[pos[i - 1]][0];
        B += sum[pos[i] - 1][1] - sum[pos[i - 1]][1];
        ++p;
    }
    return true;
}
bool dfs(int pre,int dep,int len) {
    cnt = dep;
    if(!check()) return false;
    int k = 1 + cnt;
    for(int i = 1 ; i <= cnt ; ++i) {
        if(L[i] == 1) ++k;
        else k += 2 * L[i] - 1;
    }
    int res = C[N - len + k - 1][k - 1];
    res = mul(res,fac[cnt]);
    int t = 0;
    for(int i = 1 ; i <= cnt ; ++i) {
        if(L[i] != L[i - 1]) {
            res = mul(res,invfac[t]);
            t = 0;
        }
        ++t;
    }
    res = mul(res,invfac[t]);
    update(ans,res);
    if(dep + 1 > tot) return true;
    for(int i = pre ; i <= 70 ; ++i) {
        int tl = len;
        if(dep != 0) ++tl;
        if(i == 1 || i == 2) tl += 1;
        else tl += i - 2 + i - 1;
        if(tl > N) break;
        L[dep + 1] = i;
        if(!dfs(i,dep + 1,tl)) break;
    }
    return true;
}
void Solve() {
    read(N);read(K);
    scanf("%s",s + 1);
    C[0][0] = 1;
    for(int i = 1 ; i <= 200 ; ++i) {
        C[i][0] = 1;
        for(int j = 1 ; j <= i ; ++j) {
            C[i][j] = inc(C[i - 1][j - 1],C[i - 1][j]);
        }
    }
    fac[0] = 1;
    for(int i = 1 ; i <= 200 ; ++i) fac[i] = mul(fac[i - 1],i);
    invfac[200] = fpow(fac[200],MOD - 2);
    for(int i = 199 ; i >= 0 ; --i) invfac[i] = mul(invfac[i + 1],i + 1);
    tot = 0;
    for(int i = 1 ; i <= K ; ++i) {
        if(s[i] == 'r') pos[++tot] = i;
        sum[i][0] = sum[i - 1][0];
        sum[i][1] = sum[i - 1][1];
        if(s[i] == 'r') sum[i][0]++;
        else sum[i][1]++;
    }
    dfs(1,0,0);
    out(ans);enter;
}
int main() {
#ifdef ivorysi
    freopen("f1.in","r",stdin);
#endif
    Solve();
    return 0;
}

#include <bits/stdc++.h>
using namespace std;

#define M(x) ((x)%1000000007)
#define MADD(x, y) (x = M(x + (y)))

int N, K;
char s[75];

bool reach[75][75][75];

// no. of ways of having used exactly
// A red, B blue seqs up to index i
// 0: blank, 1: red, 2: blue (last ind)
int cnt[75][75][75][3];

int main() {
	cin >> N >> K >> s;

	reach[0][0][0] = 1;
	for (int i = 0; i < K; ++i) {
		for (int A = 0; A < 75; ++A) {
			for (int B = 0; B < 75; ++B) if (reach[A][B][i]) {
				if (s[i] == 'r') {
					reach[A+1][B][i+1] = reach[A][B][i+1] = 1;
					if (B) reach[A][B+1][i+1] = 1;
					if (A) reach[A-1][B][i+1] = 1;
				} else {
					reach[A][B][i+1] = 1;
					if (A) reach[A][B+1][i+1] = 1;
					if (B) reach[A][B-1][i+1] = 1;
				}
			}
		}
	}

	cnt[0][0][0][0] = 1;
	for (int i = 0; i < N; ++i) {
		for (int A = 0; A < 72; ++A) {
			for (int B = 0; B < 72; ++B) {
				// first, consider the case
				// that we just came from a red or blue cell.
				// we could choose to break off the current
				// red sequence here, placing a white here.
				MADD(cnt[A][B][i+1][0], M(cnt[A][B][i][1] + cnt[A][B][i][2]));

				// if we came from a blank, we could start a new red sequence,
				// or start a red and a blue sequence simultaneously.
				MADD(cnt[A+1][B][i+1][1], cnt[A][B][i][0]);
				MADD(cnt[A+1][B+1][i+1][2], cnt[A][B][i][0]);

				// if we came from a red, we could start a new blue seq here
				MADD(cnt[A][B+1][i+1][2], cnt[A][B][i][1]);

				// if we came from a blue, we can go back to just being red again
				MADD(cnt[A][B][i+1][1], cnt[A][B][i][2]);

				// if we came from red, we could just keep being red
				MADD(cnt[A][B][i+1][1], cnt[A][B][i][1]);

				// if we came from a blank,
				// we can just keep being blank
				MADD(cnt[A][B][i+1][0], cnt[A][B][i][0]);

				// if we came from blue, we could just keep being blue
				MADD(cnt[A][B][i+1][2], cnt[A][B][i][2]);
			}
		}
	}

	int tot = 0;
	for (int A = 0; A < 75; ++A) {
		for (int B = 0; B < 75; ++B) if (reach[A][B][K]) {
			MADD(tot, M(M(cnt[A][B][N][0] + cnt[A][B][N][1]) + cnt[A][B][N][2]));
		}
	}

	cout << tot << endl;
}

#include<bits/stdc++.h>
using namespace std;
const int mod=1e9+7;
void ade(int& x,int y){x+=y;if(x>=mod)x-=mod;}
int add(int x,int y){return x+y<mod?x+y:x+y-mod;}
int qpow(int x,int k)
{
    int r=1;
    while(k)
    {
        if(k&1)r=1ll*r*x%mod;
        k>>=1;x=1ll*x*x%mod;
    }
    return r;
}
int C[1010][1010];
int fac[1010],ifac[1010];
void init()
{
    for(int i=0;i<=1000;++i)
    {
        C[i][0]=1;
        for(int j=1;j<=i;++j)C[i][j]=add(C[i-1][j],C[i-1][j-1]);
    }
    fac[0]=1;
    for(int i=1;i<=1000;++i)fac[i]=1ll*fac[i-1]*i%mod;
    ifac[1000]=qpow(fac[1000],mod-2);
    for(int i=999;i>=0;--i)ifac[i]=1ll*ifac[i+1]*(i+1)%mod;
}
int n,m;
int tr;
char s[110];
int a[110];
bool book[77];
bool eb[77];
bool check(int d)
{
    int cr=0,crb=0;
    for(;a[cr+1]==1&&cr+1<=d;cr++);
    crb=d-cr;
    memset(book,0,sizeof(book[0])*(m+3));
    memset(eb,0,sizeof(eb[0])*(m+3));
    int pr=0,pb=0;
    for(int t=1;t<=crb;++t)
    {
        while((s[pr]!='r'||book[pr])&&pr<=m)pr++;
        while((s[pb]!='b'||book[pb]||pb<=pr)&&pb<=m)pb++;
        if(pr>m||pb>m)return 0;
        book[pr]=book[pb]=1;
        eb[pb]=1;
    }
    int tcr=cr;
    for(int i=1;i<=m&&cr;++i)
        if(!book[i]&&s[i]=='r')cr--,book[i]=1;
    if(cr)return 0;
    for(int i=m,p=tcr+1,cnt=0;p<=d;--i)
    {
        if(!book[i])cnt++;
        if(eb[i])
        {
            cnt-=a[p]-2;
            if(cnt<0)return 0;
            p++;
        }
    }
    return 1;
}
int ans;
void calc(int d,int l)
{
    int s=d+1;
    for(int i=1;i<=d;++i)
        if(a[i]==1)s++;
        else s+=(a[i]-1)*2+1;
    int res=1ll*C[n-l+s-1][s-1]*fac[d]%mod;
    for(int i=1,j;i<=d;i=j+1)
    {
        for(j=i;a[j+1]==a[i]&&j<d;++j);
        res=1ll*res*ifac[j-i+1]%mod;
    }
    ade(ans,res);
}
bool dfs(int x,int la,int len,int sl)
{
    if(!check(x-1))return 0;
    if(x-1>tr)return 1;
    calc(x-1,len);
    int nl;
    for(int i=la;i<=m-sl;++i)
    {
        a[x]=i;
        nl=len;
        if(x!=1)nl++;
        if(i==1)nl++;
        else nl+=(i-1)*2-1;
        if(nl>n)break;
        if(!dfs(x+1,i,nl,sl+i))break;
    }
    return 1;
}
int main()
{
    init();
    scanf("%d%d",&n,&m);
    scanf("%s",s+1);
    for(int i=1;i<=m;++i)tr+=(s[i]=='r');
    dfs(1,1,0,0);
    printf("%d\n",ans);
    return 0;
}

