#define FOR(i,a,b) for(int i=a;i<=b;i++)
using namespace std;
const int N=1010,P=1e9+7;
int n,m,ans,cnt;
int a[N],b[N],c[N],d[N],f[N],p[N],fc[N],vf[N],iv[N];
char s[N];
void ini(int n){
	iv[1]=fc[0]=vf[0]=1;
	FOR(i,2,n) iv[i]=1ll*(P-P/i)*iv[P%i]%P;
	FOR(i,1,n) fc[i]=1ll*fc[i-1]*i%P,vf[i]=1ll*vf[i-1]*iv[i]%P;
}
int C(int x,int y){return x<y?0:1ll*fc[x]*vf[x-y]%P*vf[y]%P;}
void work(int L,int s,int t){
	int r=1;
	FOR(i,1,m) c[i]=0,b[i]=f[i];b[L+1]=0;
	//FOR(i,1,L) cout<<b[i]<<' ';cout<<'\n';
	FOR(i,1,m)if(!a[i] && r<=L) b[c[i]=r]--,p[r++]=i;
	if(b[r]) return;r=1;
	FOR(i,1,m)if( a[i] && b[r] && p[r]<i) b[c[i]=r]--,p[r++]=i;
	if(b[r]) return;r=1;
	FOR(i,1,m)if(!c[i] && b[r] && p[r]<i && !--b[r]) r++;
	if(b[r]) return;
	int ret=fc[L];
	FOR(i,1,m) ret=1ll*ret*vf[d[i]]%P;
	(ans+=1ll*ret*C(t,2*s)%P)%=P;
}
void dfs(int x,int s,int t){
	if(t<2*s) return;work(x-1,s,t);
	FOR(i,1,min(f[x-1],m-s)) f[x]=i,d[i]++,dfs(x+1,s+i,t+2*(i>=2)),d[i]--;
}
int main(){
	//freopen("1.in","r",stdin);
	scanf("%d%d%s",&n,&m,s+1);ini(1000);
	FOR(i,1,m) a[i]=s[i]=='b';
	f[0]=m;dfs(1,0,n+1);
	cout<<ans<<'\n';
}

#include <cstdio>
#define FOR(i, l, r) for(int i = l; i <= r; ++i)

using namespace std;

#define left LEFT
typedef long long ll;
const int N = 510;
const int mod = 1e9 + 7;

int n, k, ans, tot, left, a[N], c[N][N];
char s[N];

bool check()
{
    if (!tot) return 0;
    int now1 = 1, now2 = 1, c = 0;
    FOR(i, 1, k)
    {
        if (!(now1 <= tot || a[now2] > 1 || c)) break;
        if (now1 <= tot && s[i] == 'r') ++now1; else
        if (now2 < now1 && a[now2] > 1 && s[i] == 'b') c += a[now2++] - 2;
        else if (c) --c;
    }
    return !(now1 <= tot || a[now2] > 1 || c);
}

void update()
{
    ll tmp = 1, num = tot + 1;
    for(int i = 1, j; i <= tot; i = j + 1)
    {
        for(j = i; a[j + 1] == a[i]; ++j);
        tmp = tmp * c[j][i - 1] % mod;
        num += (j - i + 1) * (a[i] * 2 - 1);
    }
    tmp = tmp * c[num + left - 1][num - 1] % mod;
    if ((ans += tmp) >= mod) ans -= mod;
}

void dfs(int mx)
{
    if (left < 0) return;
    if (check()) update();

    if (tot) --left;
    ++tot; --left; dfs(a[tot] = 1); ++left;
    FOR(i, 2, mx)
    {
        left -= i * 2 - 3;
        dfs(a[tot] = i);
        left += i * 2 - 3;
    }
    a[tot--] = 0;
    if (tot) ++left;
}

int main()
{
    FOR(i, 0, 500)
    {
        c[i][0] = 1;
        FOR(j, 1, 500)
            if ((c[i][j] = c[i - 1][j] + c[i - 1][j - 1]) >= mod)
                c[i][j] -= mod;
    }

    scanf("%d%d%s", &n, &k, s + 1);
    left = n; dfs(100);
    printf("%d\n", (ans + 1) % mod);
    return 0;
}

