bool check(const vector<int>& ip, int sum) {
    memset(trk, 0, sizeof trk);
    memset(pos, -1, sizeof pos);
    memset(cnt, 0, sizeof cnt);
    int m = ip.size();
    for (int i = m-1; i >= 0; i--) {
        for (int j = 0; j < k; j++) {
            if (s[j]=='r' && !trk[j]) {
                pos[i] = j; trk[j] = true;
                cnt[i]++;
                break;
            }
        }
        if (pos[i] == -1) return false;
    }
    for (int i = m-1; i >= 0; i--) {
        if (ip[i] == 1) continue;
        for (int j = pos[i]+1; j < k; j++) {
            if (s[j]=='b' && !trk[j]) {
                cnt[i]++;
                pos[i]=j; trk[j] = true;
                break;
            }
        }
        if (cnt[i] != 2) return false;
    }
    for (int i = m-1; i >= 0; i--) {
        for (int j = pos[i]+1; ip[i] > cnt[i] && j < k; j++) {
            if (!trk[j]) {
                cnt[i]++;
                pos[i] = j; trk[j] = true;
            }
        }
        //cout << i << ','<<cnt[i]<<'\n';
        if (ip[i] != cnt[i]) return false;
    }

    mint coeff = c.fac[m];
    int las = 0, len = 0;
    for (const int& x: ip) {
        if (x == las) len++;
        else {
            coeff *= c.ifac[len];
            len = 1;
            las = x;
        }
    }coeff *= c.ifac[len];

    int zero = 0;
    zero += 2;
    for (const int& x: ip) {
        if (x > 1) zero+=2;
    }

    //dbg(ip, sum);
    if (sum == 0) {
        res += 1; return true;
    }
    //cout << coeff << '\n';
    res += c.p(n+zero, sum+zero) * coeff;
    //cout << res << "\n";
    return true;
}

