int solveF(int len, int cnt) {
	if (len == 0) return (cnt == 0);
	if (f[len][cnt] != -1) return f[len][cnt];

	int ret = 0;
	for (int i = 0; i <= len; i++) {
		ret += solveF(len - i, cnt - 1);
		ret %= mod;
	}
	return f[len][cnt] = ret;
}

signed main() {
	int i, j, k;

	for (i = 0; i < 200; i++) for (j = 0; j < 200; j++) f[i][j] = -1;
	for (i = 0; i < 200; i++) for (j = 0; j < 200; j++) solveF(i, j);

