							dp[r + 1][j + 1][k + x] += dp[i][j][k] * f[r - l][2 * x + 1];
							dp[r + 1][j + 1][k + x] %= mod;
						}
					}
				}
			}
		}
	}

	int ans = 0;
	for (i = 0; i <= n + 1; i++) {
		ans += dp[i][rcnt][bcnt];
		ans %= mod;
	}
	cout << ans << endl;
	return 0;
}

