u64 power(u64 a, int b) {
	u64 ans=1;
	for(; b; a=a*a%P, b/=2) if(b&1) ans=ans*a%P;
	return ans;
}

int n, k, rcnt, f[70];
u32 ans, c[500][500], fac[500], inv[500];
