	for (;!isdigit(ch);ch=getchar()) if (ch=='-') f=-1;
	for (;isdigit(ch);ch=getchar()) g=g*10+ch-'0';
	return f*g;
}
int qpow(int x,int k){
	int t=1;
	for (;k;k>>=1){
		if (k&1) t=(ll)t*x%p;
		x=(ll)x*x%p;
	}
	return t;
}
int n,m,ans,a[N],s[N],pos[N],C[N][N],po[N],inv[N];
char ch[N];bool h[N];
int get_nex(char c,int i){
	for (;(i<=m)&&(h[i]||(ch[i]!=c));i++);
	return i;
}
int get_n(int i){
	for (;(i<=m)&&h[i];i++);
	return i;
}
bool check(int dep){
	memset(h,0,m+1);
	for (int i=1;i<=dep;i++){
		pos[i]=get_nex('r',1);
		if (pos[i]>m) return false;
		h[pos[i]]=1;
	}
	for (int i=1;i<=dep;i++)
	if (a[i]>1){
		pos[i]=get_nex('b',pos[i]+1);
		if (pos[i]>m) return false;
		h[pos[i]]=1;
	}
	for (int i=1;i<=dep;i++)
	for (int j=3;j<=a[i];j++){
		pos[i]=get_n(pos[i]+1);
		if (pos[i]>m) return false;
		h[pos[i]]=1;
	}
	return true;
}
void calc(int res,int r,int dep){
	int t=po[dep-1],s1=n+1,s2=0;
	for (int i=1;i<=n;i++){
		t=(ll)t*inv[s[i]]%p;
		s1+=(i>1)*2*s[i];
		s2+=2*s[i]*i;
	}
	if ((s1<s2)||(!check(dep-1))) return;
	ans=(ans+(ll)t*C[s1][s2])%p;
	for (int i=1;i<=r;i++){
		a[dep]=i;s[i]++;
		calc(res-i,min(i,res-i),dep+1);
		s[i]--;
	}
	a[dep]=0;
}
