const int N = 75, K = 75;
const long long mod = 1000000007ll;

int n, m, stk[N], suf[K], pos[N], tmp[N], top = 0;
char str[K];
long long c[N * 3][N * 3], ans = 0ll;

long long solve () {
	for (int i = 0; i < m; i++) suf[i] = 1;
	for (int i = top - 1; i >= 0; i--) {
		int first_r = m;
		for (int j = m - 1; j >= 0; j--) {
			if (suf[j] && str[j] == 'r') first_r = j;
		}
		if (first_r >= m) return 0ll;
		if (!stk[i]) suf[pos[i] = first_r] = 0;
		else {
			int first_b = m;
			for (int j = m - 1; j >= first_r; j--) {
				if (suf[j] && str[j] == 'b') first_b = j;
			}
			if (first_b >= m) return 0ll;
			suf[first_r] = suf[pos[i] = first_b] = 0;
		}
	}

	suf[m] = 0;
	for (int i = m - 1; i >= 0; i--) suf[i] += suf[i + 1];
	for (int i = 0, j = 0; i < top; i++) {
		if (stk[i]) {
			j += stk[i] - 1;
			if (suf[pos[i]] < j) return 0ll;
		}
	}

	long long res = 1ll;
	for (int i = 0; i <= n; i++) tmp[i] = 0;
	for (int i = 0; i < top; i++) tmp[stk[i]]++;
	for (int i = 0, j = 0; i <= n; i++) {
		j += tmp[i];
		res = res * c[j][tmp[i]] % mod;
	}

	int num1 = 0, num2 = 0;
	for (int i = 0; i < top; i++) {
		if (stk[i]) num1 += 2 * stk[i] - 1, num2 += 2;
		else num1++;
	}
	if (top) num1 += top - 1, num2 += 2;
	else num1++;
	res = res * c[n + num2 - 1][num1 + num2 - 1] % mod;
	return res;
}

void dfs (int mn, int total) {
	if (mn == n + 1) ans = (ans + solve()) % mod;
	if (mn <= n) dfs(mn + 1, total);
	int t = mn ? mn << 1 : 2;
	if (total + t <= n + 1) {
		stk[top++] = mn;
		dfs(mn, total + t);
		top--;
	}
}

int main () {
	scanf("%d%d%s", &n, &m, str);
	for (int i = 0; i <= n * 3; i++) c[i][0] = c[i][i] = 1ll;
	for (int i = 1; i <= n * 3; i++) {
		for (int j = 1; j < i; j++) c[i][j] = (c[i - 1][j] + c[i - 1][j - 1]) % mod;
	}
	dfs(0, 0);
	printf("%lld\n", ans);
	return 0;
}

