using namespace std;

const int mod = 1e9 + 7;

int exp(int x, int n) {
    int ret = 1;
    while(n) {
        if(n & 1) ret = 1LL * ret * x % mod;
        x = 1LL * x * x % mod;
        n >>= 1;
    }
    return ret;
}
int inv(int x) {
    return exp(x, mod - 2);
}
int fact[500], invf[500];

int comb(int n, int k) {
    return 1LL * fact[n] * invf[k] % mod * invf[n - k] % mod;
}
int H(int n, int k) {
    return comb(n + k - 1, k);
}

int N, K;
string S;
vector<int> R, B;

struct Group {
    vector<int> f;
};
vector<Group> group;
Group tmp;

void dfs(int p, int g, int f) {
    if(p >= N) {
        group.push_back(tmp);
        return;
    }
    if(f) {
        group.push_back(tmp);
    }
    if(p + 2*g > N + 1) return;

    dfs(p, g + 1, 0);

    if(max(p + 2*g, p + 2) <= N + 1) {
        tmp.f.push_back(g);
        dfs(max(p + 2*g, p + 2), g, 1);
        tmp.f.pop_back();
    }
}

bool chk(int len) {
    int pos1 = 0, pos2 = 0;
    for(int i = 0; i < len; i++) {
        while(pos2 < B.size() && R[pos1] > B[pos2]) pos2++;

        if(pos2 == B.size()) return false;
        pos1++;
        pos2++;
    }
    return true;
}

int main() {
    fact[0] = 1;
    for(int i = 1; i < 500; i++) {
        fact[i] = 1LL * fact[i - 1] * i % mod;
    }
    for(int i = 0; i < 500; i++) {
        invf[i] = inv(fact[i]);
    }

    cin >> N >> K >> S;

    for(int i = 0; i < S.size(); i++) {
        if(S[i] == 'r') R.push_back(i);
        else B.push_back(i);
    }

    dfs(0, 0, 1);

    int ans = 0;
    for(int g = 0; g < group.size(); g++) {
        vector<int> f = group[g].f;

        int sum = 0;
        int x = 0;
        for(int i = 0; i < f.size(); i++) {
            sum += f[i];

            if(f[i] == 0) x++;
            else x += 2*f[i] - 1;
        }

        if(f.size() > R.size()) continue;
        if(sum + f.size() > K) continue;
        if(N - x - (int)f.size() + 1 < 0) continue;

        int t = fact[ f.size() ];

        int cnt = 0;
        for(int i = 0; i < f.size(); i++) {
            cnt++;
            if(i == f.size() - 1 || f[i] != f[i + 1]) {
                t = 1LL * t * invf[cnt] % mod;
                cnt = 0;
            }
        }

        t = 1LL * t * H(2*sum + 2*f.size() + 1, N - x - (int)f.size() + 1) % mod;

        int len = 0;
        for(int i = 0; i < f.size(); i++) {
            if(f[i] != 0) len++;
        }

        if(chk(len)) {
            ans += t;
            ans %= mod;
        }
    }

    cout << ans;
}

