	for (int i = 1, j = 1; i <= k; ++i)
		if (s[i] == 'r') r.push_back(i);
	for (int i = fac[0] = 1; i <= 2 * n + 1; ++i) fac[i] = 1ll * fac[i - 1] * i % P;
	ifac[2 * n + 1] = power(fac[2 * n + 1], P - 2);
	for (int i = 2 * n + 1; i; --i) ifac[i - 1] = 1ll * ifac[i] * i % P;
	dfs(1, 0);
	printf("%d\n", ans);
	return 0;
}

