	}
	for (int i = 1; i <= n * 5; i++) {
		for (int j = 1; j < i; j++) c[i][j] = (c[i - 1][j] + c[i - 1][j - 1]) % mod;
	}
	dfs(0, 0);
	printf("%lld\n", ans);
	return 0;
}

