int inc(int a,int b) {
    return a + b >= MOD ? a + b - MOD : a + b;
}
int mul(int a,int b) {
    return 1LL * a * b % MOD;
}
void update(int &x,int y) {
    x = inc(x,y);
}
int fpow(int x,int c) {
    int res = 1,t = x;
    while(c) {
        if(c & 1) res = mul(res,t);
        t = mul(t,t);
        c >>= 1;
    }
    return res;
}
bool check() {
    if(tot < cnt) return false;
    memset(sum,0,sizeof(sum));
    int p = 1;
    for(int i = cnt ; i >= 1 ; --i) {
	sum[pos[i]]++;
	if(L[p] >= 2) {
	    sum[matc[i]] += L[p] - 1;
	}
    }
    for(int i = K ; i >= 1 ; --i) {
	sum[i] += sum[i + 1];
	if(sum[i] > K - i + 1) return false;
    }
    return true;
}
