    for (int i = 1; i < kM; ++ i) {
        F[i] = F[i - 1] * 1LL * i % MOD;
        Finv[i] = Finv[i - 1] * 1LL * Inv[i] % MOD;
    }
    scanf("%d%d%s", &n, &k, s);
    printf("%d\n", search(1, 0, 0, 0));
}

