bool vis[maxn];
ll ans,fac[maxn],inv[maxn],c[maxn*10][maxn*10];

inline ll qpow(ll a,ll n){
	ll res=1;
	while(n){
		if(n&1ll)
			res=res*a%mod;
		a=a*a%mod;
		n>>=1;
	}
	return res;
}
void init(){
	fac[0]=1;
	for(int i=1;i<maxn;++i)
		fac[i]=fac[i-1]*i%mod;
	inv[maxn-1]=qpow(fac[maxn-1],mod-2);
	for(int i=maxn-1;i;--i)
		inv[i-1]=inv[i]*i%mod;
	for(int i=0;i<maxn*10;++i){
		c[i][0]=1;
		for(int j=1;j<=i;++j)
			c[i][j]=(c[i-1][j-1]+c[i-1][j])%mod;
	}
}
void judge(int rest){
	m=0;
	for(int i=n;~i;--i)
		for(int j=0;j<cnt[i];++j)
			a[m++]=i;
	if(m==0){
		ans=(ans+1)%mod;
		return;
	}
	memset(vis,0,sizeof(vis));
	for(int i=0,j=0;i<m;++i){
		while(j<k&&s[j]=='b')
			++j;
		if(j==k)
			return;
		vis[j]=true;
		beg[i]=++j;
	}
	for(int i=0,j=0;i<m&&a[i]>=1;++i){
		if(j<beg[i])j=beg[i];
		while(j<k&&(s[j]=='r'||vis[j]))
			++j;
		if(j==k)
			return;
		vis[j]=true;
		beg[i]=++j;
	}
	for(int i=0,j=0;i<m&&a[i]>=2;++i){
		if(j<beg[i])j=beg[i];
		int rest=a[i]-1;
		while(rest){
			while(j<k&&vis[j])
				++j;
			if(j==k)
				break;
			else
				vis[j]=true,--rest;
		}
		if(rest)
			return;
		++j;
	}
	ll res=fac[m];
	for(int i=0;i<=n;++i)
		res=res*inv[cnt[i]]%mod;
	int box=1;
	for(int i=0;i<m;++i)
		if(a[i])
			box+=2*a[i]+2;
		else
			box+=2;
	res=res*c[box+rest-1][rest]%mod;
	ans=(ans+res)%mod;
}
void dfs(int pos,int rest){
	if(!pos){
		for(int i=0;i*2<=rest;++i){
			cnt[0]=i;
			judge(rest-i*2);
		}
		return;
	}
	for(int i=0;i*2*pos<=rest;++i)
		cnt[pos]=i,dfs(pos-1,rest-i*2*pos);
}

