	fo(x,0,n)
	{
		fo(y,0,n)
		if (x || y)
		{
			memset(bz,0,sizeof(bz)),k=l=0;
			fo(i,1,x)
			{
				fo(j,1,m) if (!bz[j] && a[j]=='r') break;
				fo(k,j,m) if (!bz[k] && a[k]=='b') break;
				if (k>m) break;
				bz[j]=bz[k]=1;
			}
			if (k>m) continue;
			fo(i,1,m) if (l<y && !bz[i] && a[i]=='r') ++l,bz[i]=1;
			if (l<y) continue;

			l=0;
			fd(j,m,1)
			if (bz[j] && a[j]=='b')
			{
				d[++l]=0;
				fo(k,j,m) d[l]+=!bz[k];
			}

			memset(f,0,sizeof(f)),f[0][0][0]=1;
			fo(i,0,x-1)
			{
				fo(j,0,d[x])
				{
					fo(k,0,d[x])
					if (f[i][j][k])
					{
						if (k<d[x]) add(f[i][j][k+1],f[i][j][k]);
						fo(l,1,x-i)
						if (j+l*k<=d[i+l])
						add(f[i+l][j+l*k][k+1],f[i][j][k]*C(i+l+y,l));
						else break;
					}
				}
			}

			fo(j,0,d[x])
			{
				fo(k,0,d[x]+1)
				{
					fo(l,0,n)
					add(ans,f[x][j][k]*C(l+(y+2*j+x*3)-1,(y+2*j+x*3)-1)%mod*C(n-(x+y-1)-2*j-l,x+y));
				}
			}
		}
	}
	printf("%lld\n",ans);

	fclose(stdin);
	fclose(stdout);
	return 0;
}

#include "bits/stdc++.h"
using namespace std;
typedef long long ll;
const ll MOD = 1000000007LL;
const ll INF = 1LL << 60;
const double PI = 3.141592653589793238;
const double EPS = 1e-10;
ll num[71][71];
ll fact[10001];
ll inv_fact[10001];
ll mod_pow(ll a, ll b) {
	if (b == 0) return 1;
	if (b % 2 == 0) {
		ll x = mod_pow(a, b / 2);
		return (x*x) % MOD;
	}
	return (a*mod_pow(a, b - 1)) % MOD;
}
ll comb(ll n, ll k) {
	if (n < k) return 0;
	ll ret = fact[n];
	(ret *= inv_fact[k]) %= MOD;
	(ret *= inv_fact[n - k]) %= MOD;
	return ret;
}
int N, K;
ll ans;
string s;
bool check(vector<int> &v) {
	bool used[70] = {};
	int pos[70];
	int now= 0;
	for (int i = 0; i < v.size(); i++) {
		while (now < s.size()) {
			if (s[now] == 'r') break;
			now++;
		}
		if (now == s.size()) return 0;
		used[now] = 1;
		pos[i] = now;
		now++;
	}
	for (int i = 0; i < v.size(); i++) {
		if (v[i] == 1) break;
		now = pos[i] + 1;
		while (now < s.size()) {
			if (!used[now] && s[now] == 'b') break;
			now++;
		}
		if (now == s.size()) return 0;
		used[now] = 1;
		pos[i] = now;
	}
	for (int i = 0; i < v.size(); i++) {
		if (v[i] <= 2) break;
		int cnt = 0;
		now = pos[i] + 1;
		while (now < s.size()) {
			if (!used[now]) {
				used[now] = 1;
				cnt++;
			}
			if (cnt >= v[i] - 2) break;
			now++;
 		}
		if (now == s.size()) return 0;
	}
	return 1;
}
ll calc(vector<int> &v) {
	map<int, int> freq;
	for (int i = 0; i < v.size(); i++) {
		freq[v[i]]++;
	}
	ll ret = fact[v.size()];
	for (auto p : freq) {
		(ret *= inv_fact[p.second]) %= MOD;
	}
	int x = N + 1;
	int y = 0;
	for (int i = 0; i < v.size(); i++) {
		if (v[i] >= 2) x += 2;
		y += 2 * v[i];
	}
	(ret *= comb(x, y)) %= MOD;
	return ret;
}
void brute(int k, int prev, int sum, vector<int> &v) {
	if (sum > N) return;
	if (check(v)) {
		(ans += calc(v)) %= MOD;
	}
	for (int i = min(k, prev); i >= 1; i--) {
		int m = 1;
		if (i > 1) m = i * 2 - 3;
		if (v.size() > 0) m++;
		v.push_back(i);
		brute(k - i, i, sum + m, v);
		v.pop_back();
	}
}
int main() {
	fact[0] = inv_fact[0] = 1;
	for (int i = 1; i <= 10000; i++) {
		fact[i] = (fact[i - 1] * i) % MOD;
		inv_fact[i] = (inv_fact[i - 1] * mod_pow(i, MOD - 2)) % MOD;
	}
	cin >> N >> K;
	cin >> s;
	vector<int> v;
	brute(K, K, 0, v);
	cout << ans << endl;
}

