#include <bits/stdc++.h>
#define debug(x) cerr << #x << " " << (x) << endl
using namespace std;

const int N = 75, K = 75;
const long long mod = 1000000007ll;

int n, m, stk[N], pos[N], suf[K], tmp[N], top = 0;
char str[K];
bool vis[K];
long long c[N * 3][N * 3], ans = 0ll;

bool check () {
	vector<int> vec1, vec2, vec3;
	for (int i = 0; i < top; i++) vec1.push_back(stk[i]);
	for (int i = 0; i < m; i++) {
		if (str[i] == 'r') {
			if (!vec1.empty()) {
				if (vec1.back()) vec2.insert(vec2.begin(), vec1.back());
				vec1.pop_back();
			}
			else if (!vec3.empty()) {
				(*vec3.rbegin())--;
				if (!vec3.back()) vec3.pop_back();
			}
		}
		else {
			if (!vec2.empty()) {
				if (vec2.back() > 1) vec3.push_back(vec2.back() - 1);
				vec2.pop_back();
			}
			else if (!vec3.empty()) {
				(*vec3.rbegin())--;
				if (!vec3.back()) vec3.pop_back();
			}
		}
	}
	return vec1.empty() && vec2.empty() && vec3.empty();
}
long long solve () {
	if (!check()) return 0ll;
	long long res = 1ll;
	for (int i = 0; i <= n; i++) tmp[i] = 0;
	for (int i = 0; i < top; i++) tmp[stk[i]]++;
	for (int i = 0, j = 0; i <= n; i++) {
		j += tmp[i];
		assert(j <= n * 3 && tmp[i] <= n * 3);
		res = res * c[j][tmp[i]] % mod;
	}

	int num1 = 0, num2 = 0;
	for (int i = 0; i < top; i++) {
		if (stk[i]) num1 += 2 * stk[i] - 1, num2 += 2;
		else num1++;
	}
	if (top) num1 += top - 1, num2 += 2;
	else num1++;
	if (n + num2 - 1 < 0 || num1 + num2 - 1 < 0) return 0ll;
	if (n + num2 - 1 > n * 3 || num1 + num2 - 1 > n * 3) return 0ll;
	res = res * c[n + num2 - 1][num1 + num2 - 1] % mod;
	return res;
}

void dfs (int mn, int total) {
	if (mn == n + 1) ans = (ans + solve()) % mod;
	if (mn <= n) dfs(mn + 1, total);
	int t = mn ? mn << 1 : 2;
	if (total + t <= n + 1) {
		stk[top++] = mn;
		dfs(mn, total + t);
		top--;
	}
}

