	for (int i = 0; i <= n * 3; i++) c[i][0] = c[i][i] = 1ll;
	for (int i = 1; i <= n * 3; i++) {
		for (int j = 1; j < i; j++) c[i][j] = (c[i - 1][j] + c[i - 1][j - 1]) % mod;
	}
	dfs(0, 0);
	printf("%lld\n", ans);
	return 0;
}

