			update(dp[i][j][k][l][0], tmp); // 0 + w
			if (j) update(dp[i][j - 1][k][l][1], tmp); // 0 + r
			if (j && k && l) update(dp[i][j - 1][k - 1][l - 1][2], tmp); // 0 + b

			tmp = dp[i - 1][j][k][l][1];
			update(dp[i][j][k][l][0], tmp); // 1 + w
			update(dp[i][j][k][l][1], tmp); // 1 + r
			if (k && l) update(dp[i][j][k - 1][l - 1][2], tmp); // 1 + b

			tmp = dp[i - 1][j][k][l][2];
			update(dp[i][j][k][l][0], tmp); // 2 + w
			update(dp[i][j][k][l][3], tmp); // 2 + r
			update(dp[i][j][k][l][2], tmp); // 2 + b

			tmp = dp[i - 1][j][k][l][3];
			update(dp[i][j][k][l][0], tmp); // 3 + w
			update(dp[i][j][k][l][3], tmp); // 3 + r
			if (l) update(dp[i][j][k][l - 1][2], tmp); // 3 + b
		}
	}
	int ans = 0;
	for (int i = 0; i <= 3; i++)
		update(ans, dp[n][0][0][0][i]);
	writeln(ans);
	return 0;
}

