typedef long long ll; 
inline ll quick_pow(ll a, int n)
{
	ll res = 1; 
	while (n)
	{
		if (n & 1)
			res = res * a % MOD; 
		a = a * a % MOD; 
		n >>= 1; 
	}
	return res; 
}
ll ans, fact[305], inv[305]; 
inline ll C(int n, int m)
{
	return fact[n] * inv[m] % MOD * inv[n - m] % MOD; 
}
int seq[305], n, k; 
char str[305]; 
inline void work(int len, int sum)
{
	int cur_one = 0, cur_two = 0, need = 0, arb = 0; 
	for (int i = 0; i < k; i++)
	{
		if (str[i] == 'r' && cur_one < len)
			need += seq[cur_one++] != 1; 
		else if (str[i] == 'b' && need)
		{
			need--; 
			arb += seq[cur_two++] - 2; 
		}
		else if (arb)
			arb--; 
	}
	if (cur_one < len || need || arb)
		return;
	ll res = fact[len], pos = len + 1; 
	for (int i = 0, j = 0; i < len; i = j)
	{
		while (j < len && seq[i] == seq[j])
			j++; 
		res = res * inv[j - i] % MOD; 
		pos += (j - i) * (seq[i] * 2 - 1); 
	}
	(ans += res * C(n - sum + pos - 1, pos - 1)) %= MOD; 
}
void dfs(int cur, int len, int sum)
{
	if (sum > n)
		return; 
	work(len, sum); 
	for (int i = cur; i; i--)
	{
		seq[len] = i; 
		dfs(i, len + 1, sum + std::max(1, i * 2 - 3) + (len != 0)); 
	}
}
