}

ll C(ll x, ll y) {
    if(!y) return 1;
    if(x < 0 || y < 0 || x < y) return 0;
    return Fac[x] * Rev[y] % P * Rev[x - y] % P;
}

void calc(int sz) {
    int tb = 0, tr = 0;
