    }
    int t = 0, rw = 0, bw = 0;
    for(int i = 1; i <= sz; i++)
        if(X[i] == 1) {
            rw++;
            t++;
        } else {
            bw += X[i] - 1;
            rw += X[i];
            t += X[i] * 2 - 3;
        }
    t = n + 1 - t - sz;
    if(t < 0) return;
    ll tmp = Fac[sz];
