const int mod = 1e9 + 7;

int exp(int x, int n) {
    int ret = 1;
    while(n) {
        if(n & 1) ret = 1LL * ret * x % mod;
        x = 1LL * x * x % mod;
        n >>= 1;
    }
    return ret;
}
int inv(int x) {
    return exp(x, mod - 2);
}
int fact[500], invf[500];

int comb(int n, int k) {
    return 1LL * fact[n] * invf[k] % mod * invf[n - k] % mod;
}
int H(int n, int k) {
    return comb(n + k - 1, k);
}

int N, K;
string S;
vector<int> R, B;

struct Group {
    vector<int> f;
};
vector<Group> group;
Group tmp;

void dfs(int p, int g, int f) {
    if(p >= N) {
        group.push_back(tmp);
        return;
    }
    if(f) {
        group.push_back(tmp);
    }
    if(p + 2*g > N + 1) return;

    dfs(p, g + 1, 0);

    if(max(p + 2*g, p + 2) <= N + 1) {
        tmp.f.push_back(g);
        dfs(max(p + 2*g, p + 2), g, 1);
        tmp.f.pop_back();
    }
}

bool chk(int len) {
    int pos1 = 0, pos2 = 0;
    for(int i = 0; i < len; i++) {
        while(pos2 < B.size() && R[pos1] > B[pos2]) pos2++;

        if(pos2 == B.size()) return false;
        pos1++;
        pos2++;
    }
    return true;
}

