    fact[0] = 1;
    for(int i = 1; i < 500; i++) {
        fact[i] = 1LL * fact[i - 1] * i % mod;
    }
    for(int i = 0; i < 500; i++) {
        invf[i] = inv(fact[i]);
    }

    cin >> N >> K >> S;

    for(int i = 0; i < S.size(); i++) {
        if(S[i] == 'r') R.push_back(i);
        else B.push_back(i);
    }

    dfs(0, 0, 1);

    int ans = 0;
    for(int g = 0; g < group.size(); g++) {
        vector<int> f = group[g].f;

        int sum = 0;
        int x = 0;
        for(int i = 0; i < f.size(); i++) {
            sum += f[i];

            if(f[i] == 0) x++;
            else x += 2*f[i] - 1;
        }

        if(f.size() > R.size()) continue;
        if(sum + f.size() > K) continue;
        if(N - x - (int)f.size() < 0) continue;

        int t = fact[ f.size() ];

        int cnt = 0;
        for(int i = 0; i < f.size(); i++) {
            cnt++;
            if(i == f.size() - 1 || f[i] != f[i + 1]) {
                t = 1LL * t * invf[cnt] % mod;
                cnt = 0;
            }
        }

        t = 1LL * t * H(2*sum + f.size() + f.size() + 1, N - x - (int)f.size() + 1) % mod;

        int len = 0;
        for(int i = 0; i < f.size(); i++) {
            if(f[i] != 0) len++;
        }

        if(chk(len)) {
            ans += t;
            ans %= mod;
        }
    }

    cout << ans;
}

