		if(pos < v.size()) rep(i,s.length()) if(!used[i] && b[pos] < i){
			used[i] = true;
			c[pos]++;
			if( c[pos] >= v[pos] / 2 ){
				pos++;
				while(pos < v.size() && v[pos] < 3) pos++;
				if(pos >= v.size()) break;
			}
		}
		if(pos < v.size()) continue;

		//cout << v << "\t";
		// ok
		ll less = N;
		for(auto t: v) less -= t + 1;
		for(auto t: v) if(t == 2) less++;
		if( !v.empty() ) less++;
		//cout << "less=" << less << " ";

		ll num = v.size() + 1;  // for White
		for(auto t: v){  // for Red
			if(t == 1) num++;
			else if(t == 2) num += 2;
			else num += t / 2 + 2;
		}
		for(auto t: v){  // for Blue
			if(t == 1) ;
			else if(t == 2) num += 1;
			else num += t / 2 + 1;
		}
		//cout << "num=" << num << " ";

		ll sum = fact[v.size()];
		ll bf = -1, cnt=0;
		v.push_back(-1);
		for(auto t: v){
			cnt++;
			if(bf != t){
				//cout << "c=" << cnt << " ";
				sum = (sum * fact.inv[cnt]) % MOD;
				cnt = 0;
				bf = t;
			}
		}
		//cout << sum << " ";
		sum = (sum * cmb(num + less - 1, less)) % MOD;
		ans = (ans + sum) % MOD;
		//cout << "sum=" << sum << endl;
	}

	cout << ans << endl;

	return 0;
}

