ll quick(ll a,ll b)
{
    ll ret=1;
    while(b)
    {
        if(b&1) ret=ret*a%mod;
        a=a*a%mod;
        b>>=1;
    }
    return ret;
}
ll inv(ll a){return quick(a,mod-2);}
void init()
{
    fac[0]=1;
    invfac[0]=1;
    for(int i=1;i<maxn;i++)
    {
        fac[i]=1LL*fac[i-1]*i%mod;
        invfac[i]=inv(fac[i]);
    }
    for(int i=0;i<maxn;i++)
    {
        C[i][0]=1;
        for(int j=1;j<=i;j++)
            C[i][j]=(C[i-1][j-1]+C[i-1][j])%mod;
    }
}
