int solve()
{
    if(!f.size()) return 1;
    int need=0,needb=0;
    int p=0;
    for(int i=f.size()-1;i>=0;i--)
    {
        need+=f[p]-1;
        if(f[p]>1) needb++;
        if(suf[apos[i]]+apos.size()-f.size()<need||suf[apos[i]]<needb) return 0;
        p++;
    }
    int L=f.size()-1;
    int cnt=f.size()+1;
    for(int i=0;i<f.size();i++)
    {
        if(f[i]==1) 
        {
            L++;
            cnt++;
        }
        else {
            L+=2*f[i]-3;
            cnt+=2*f[i]-1;
        }
    }
    //dbg(f);
    int ret=comb(n-L+cnt-1,cnt-1);
    //dbg(ret,L,cnt);
    return ret;
}
int dfs(int r,int mx,int cc,int len=0)
{
    if(len+(int)f.size()-1>n) return 0;
    int ret=1LL*solve()*invfac[cc]%mod*fac[f.size()]%mod;
    if(r==0) return ret;
    if(f.size()>=ca) return ret;
    for(int i=mx;i<=r;i++)
    {
        f.push_back(i);
        int cur=dfs(r-i,i,(i==mx)?cc+1:1,i==1?len+1:len+2*i-3);
        if(i!=mx) cur=1LL*cur*invfac[cc]%mod;
        ret=(ret+cur)%mod;
        f.pop_back();
    }
    return ret;
}
