int p[N],m;
ll ans;
bool judge(){
	static int q[N],need[N];
	static int used[N];
	int head=1,tail=0;
	memset(used,255,sizeof(int)*(K+1));
	int i=1,j=1;
	for (;j<=K && i<=m && p[i]>1;++j){
		if (head<=tail && str[j]=='b')
			used[j]=p[i++]-2,used[q[head++]]=0;
		else if (str[j]=='r')
			q[++tail]=j;
	}
	if (i<=m && p[i]>1) return 0;
	for (;j<=K;++j)
		if (str[j]=='r')
			q[++tail]=j;
	for (;i<=m;++i){
		if (head>tail) return 0;
		used[q[head++]]=0;
	}
	int cnt=0;
	for (int j=K;j>=1;--j){
		if (used[j]==-1)
			cnt++;
		else if (used[j]>0){
			if (cnt<used[j]) return 0;
			cnt-=used[j];
		}
	}
	return 1;
}
ll calc(){
//	for (int i=1;i<=m;++i)
//		printf("%d ",p[i]);
//	printf("\n");
	if (m==0) return 1;
	p[m+1]=-1;
	ll s=fac[m];
	for (int i=1,j=0;i<=m;++i){
		j++;
		if (p[i]!=p[i+1]){
			s=s*ifac[j]%mo;
			j=0;
		}
	}
	ll c0=2,c1=m-1;
	for (int i=1;i<=m;++i){
		if (p[i]==1)
			c1++;
		else{
			c1+=2*p[i]-3;
			c0+=2;
		}
	}
	s=s*C((n-c1)+(c1+c0)-1,n-c1)%mo;
//	printf("ans=%lld\n",s);
	return s;
}
void dfs(int s,int k){
